DROP TABLE if EXISTS
news, categories, goods, appeals, goodToCategories;

CREATE TABLE news (
	id INT NOT NULL AUTO_INCREMENT,
	anounce TEXT NOT NULL,
	header VARCHAR(255) NOT NULL,
	content TEXT NOT NULL,
	dt DATE NOT NULL,
	PRIMARY KEY (id)
);

CREATE TABLE categories (
	id INT NOT NULL AUTO_INCREMENT,
	name VARCHAR(255) NOT NULL,
	img VARCHAR(255),
	PRIMARY KEY (id)
);

CREATE TABLE goods (
	id INT NOT NULL AUTO_INCREMENT,
	name VARCHAR(255) NOT NULL,
	description TEXT,
	price FLOAT NOT NULL,
	img VARCHAR(255),
	mainCategoryID INT NOT NULL,
	PRIMARY KEY (id),
	FOREIGN KEY (mainCategoryID) REFERENCES categories (id) ON DELETE CASCADE
);

CREATE TABLE appeals (
	id INT NOT NULL AUTO_INCREMENT,
	userName VARCHAR(255) NOT NULL,
	email VARCHAR(255) NOT NULL,
	phone VARCHAR(20),
	message TEXT NOT NULL,
	dt DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY (id)
);

CREATE TABLE goodToCategories (
	goodID INT NOT NULL,
	categoryID INT NOT NULL,
	FOREIGN KEY (goodID) REFERENCES goods (id) ON DELETE CASCADE,
	FOREIGN KEY (categoryID) REFERENCES categories (id) ON DELETE CASCADE
);




